import { useState } from "react";
import './App.css';
import mockedResponse from './dataArrey.js'

function delay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}

export default function App() {
    const [isLoading, setLoading] = useState(false);
    const [serverResponse, setServerResponse] = useState({});

    const getRates = () => delay(2000)
        .then(() => Promise.resolve())
        .then(() => setServerResponse(mockedResponse));

    const handleButtonClick = () => {
        setLoading(true);
        getRates().then(() => {
            setLoading(false)
        })
    };

    const ratesList = serverResponse.rates && Object.entries(serverResponse.rates);

    if (isLoading) return <h1>Loading</h1>;

    return (
        <div className="App">
            <button onClick={handleButtonClick}>Hello CodeSandbox</button>
            {ratesList?.length && ratesList.map(([currency, amount]) => (
                <div key={currency}>
                    <div>{currency}</div>
                    <div>{amount}</div>
                </div>
            ))}
            <h2>Start editing to see some magic happen!</h2>
        </div>
    );
}
